        Node currNode = root;
        while (currNode != null || !stack.isEmpty()){
            while(currNode != null){
                visit(currNode);
                stack.push(currNode);
                currNode = currNode.left;
            }

            if (!stack.isEmpty()){
                currNode = stack.pop();
                currNode = currNode.right;
            }
        }
    }

